<template>
  <div class="home">
    <div class="d-flex justify-content-center align-items-center mt-5">
      <div>

      <img class="turtle-img" src="data:image/jpeg;base64,">
      <img width="100" style="transform: translate(-30px,-70px);" src="../assets/turtletalk.svg">
      </div>
      <div>
        <h2 class="text-left">海龜湯解謎遊戲</h2>
        <h6 class="text-left">這個光怪陸離的世界上，發生了許多事，等著你來一探究竟...</h6>
        <div class="d-flex mt-2">
          <b-button v-if="user.loggedIn" v-b-toggle.collapse-roomlist class="mr-5">Go to Chat</b-button>
          <b-button v-if="user.loggedIn" v-b-modal.createRoom>Create A new room</b-button>
        </div>
      </div>
    </div>
    <div>

    </div>
    <b-collapse v-if="user.loggedIn" id="collapse-roomlist" class="mt-2">
      <b-card>
        <p v-for="(room,index) in rooms" :key="index" class="card-text" @click="goChat(room.name)">{{room.name}}</p>
      </b-card>
    </b-collapse>
    <!-- Modal Component -->
    <create-room v-if="user.loggedIn"/>

  </div>
</template>

<script>
import { db } from '../db.js'

import CreateRoom from './CreateRoom';
export default {
  name: 'Home',
  components: {
    CreateRoom
  },
  data() {
    return {
      rooms: [],
    }
  },
  firestore() {
    return {
      rooms: db.collection('rooms')
    }
  },
  methods: {
    goChat(room) {
      this.$router.push({name: 'chat', params: {roomtitle: room}});
    }
  },
  computed: {
    user() {
      return this.$store.state.user 
    }
  }

}
</script>

